      console.log('--incoming_route_handler: Toolbox app--');

      // Get internal Identity node (how to locate myself) 
      // - does NOT provide auth 

      let privateIdentityData = {};
      let connectionMethodNodeData = {}
      try {
        let identityNodes = await universe.searchMemory({
          cache: 'internal_private_identity',
          filter: {
            sqlFilter: {
              // nodeId: null // OLD: root-level
              type: 'identity_private:0.0.1:local:3298f2j398233',
            },
          }
        });
        privateIdentityData = identityNodes[0].data;
        let connectionMethodNode = identityNodes[0].nodes.find(node=>{return node.type == 'private_identity_connect_method:0.0.1:local:9081j29h3'});
        connectionMethodNodeData = connectionMethodNode.data;
      }catch(err){
        console.error('Failed private identity finding:', err);
      }

      let identityNode = {  
         "type":"external_identity:0.0.1:local:8982f982j92",
         "data":{  
            "identity": privateIdentityData.identity,
            "publicKey": privateIdentityData.public,
         },
         "nodes":[  
            {  
               "type":"external_identity_connect_method:0.0.1:local:382989239hsdfmn",
               "data": connectionMethodNodeData,
            }
         ]
      };

      identityNode = Buffer.from(JSON.stringify(identityNode)).toString('base64');

      let page = `
        <!DOCTYPE html>
        <html>
          <head>
            <meta charset="utf-8">
